INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (427, '百卉谷生态景园', '131508', '地址：桂林市资源县资江漂流景区终点地段', '全天开放', '4.7', ' 百卉生态景园是资江漂流终点段最精华区域，其方圆5公里，山形峻秀，岩壑多奇，源于典型的丹霞地貌，有三十八岩， 十九涧、二潭、六泉、八石等。主峰“三娘石”宛如一柱擎天，在海拔630米的 神仙寨顶有方圆50亩的原始森林，属国内罕见。为探山顶之谜，已于悬崖峭壁之上开出一条1098级险如华山的石梯游路，使人攀高试险，寻胜探幽。 景区内诸多淡红色的石柱悬90°角，还有“天脊”、“一线天”、“忘忧泉”、“桃花岛” 、“天下第一药谷”、“ 神农度假山庄”等20多处绝好佳景汇聚成一个美若仙界的景观群落。 ', 'ziyuan980', '', '', '');
