INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (434, '北帝山峡谷漂流', '1708522', '地址：贵港市平南县大鹏镇景华村', '景区暂时停业，详情请以景区公示为准；原开放时间9:00-16:00', '4.1', ' 景区处在风景秀丽、环境怡人的鹏山风光（即平南县大鹏镇和国安乡）范围内，总面积2700公顷，具有“险、奇、秀、趣”等自然景观特色，素有广西“张家界”风光之称。景区重点开设生态漂流、旅游观光、农家美食、商务会议和休闲度假等项目。景华生态漂流全长5300米落差300多米，两岸奇峰秀石，绿树成荫，风景如画，堪称华南第一漂。景区主要景点有：狮子山、双鸳鸯树、小雄鹰瀑布、夫妻石、姜太公钓鱼、亚公抱孙、猴子守鳖、悬崖走廊、金龙吐珠等。景区交通十分便利，处在广西桂东南至西北的中转站，距新修的梧州至柳州高速公路出口仅5公里。高速公路通车后，景区高速公路直达珠江三角洲、港澳地区、南宁、柳州、桂林、湖南等地。 ', 'pingnan1398', '', ' 0775-7678888,0775-7922222 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (435, '平南雄森动物大世界', '5078351', '贵港市平南县349县道北100米', '8:00-17:00', '5', '[平南雄森动物大世界位于平南县城区南侧平南县工业园区内，东面为211省道，主要配套项目有动物幼儿园、天鹅湖、大象乐园、猛兽区、天桥观光长廊、黑熊乐园、猴乐园、恐龙百鸟园、鸵鸟园、长颈鹿斑马馆、鳄鱼馆、两栖动物馆、大马戏演艺中心、农家乐餐厅、俄罗斯特色餐厅等。,  , 一进园区就是萌萌哒的动物幼儿园了，老虎、非洲狮、亚洲黑熊的小宝宝都在这里啦。萌态百出的它们是您来到雄森动物大世界见到的第一站小动物哦~,  , 它, 是南非的国兽, 是世界上最高的动物, 是世界上最优雅的动物, 是世界上脖子最长的动物, 是动物界当之无愧的大长腿, 它就是长颈鹿，还有斑马为邻, 它们都是非洲大草原上的土著哦！,  , , , , , ]', 'pingnan1398', '', '', '');
