INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (464, '大连城', '2487796', '崇州市凭祥市连城路大连城景区', '全年 08:00-17:30。', '4.1', '[大连城位于凭祥市北面1.5公里处，又名大垒城，简称连城。这里风光旖旎，洞奇石秀，是广西别具特色的旅游胜地，是国家级重点文物保护单位。中法战争结束后，朝廷任命苏元春为广西边防提督，苏元春考虑到巩固边防的需要，将提督府从柳州迁到龙州。因龙州离镇南关太远，为便于指挥和调动军队，便在凭祥开辟大连城，建立提督行署并亲自坐镇防守。大连城就这样成为了当时的广西边防军事指挥中心，相当于现在的广西军区司令部。,  , 白玉洞是一处天然山洞，因洞中的钟乳石光洁晶莹，璀灿如玉，剔透玲珑而得名，是凭祥旧八景之一，叫“玉洞天然”。1885年苏元春督办广西边防军务，第二年开始带头捐奉修建大连城、白玉洞，1903年离开，在这里驻守了十九年，以大连城为广西全边的指挥中心，同时选择在白玉洞建“养心处”和军机要地，作为研究军机和避暑的地方。,  , 白玉洞最初是凭祥群众求神拜仙的场所，苏元春是一位钟情山水的将领，他进驻白玉洞后，对白玉洞进行精心加工、修整，使洞内的景色更加神秘而迷人。整个洞府可分为三层洞天，每层洞天之间都有通道相连。洞中有洞、洞外有洞，洞洞相连，可容纳数千人。白玉洞冬暖夏凉，安全保密，是兵家指挥机关的理想场所。,   ]', 'pingxiang840', '', '', '');
