INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (477, '石跳桥', '1833519', '地址：贺州市昭平县黄姚古镇内', '全天开放', '4.3', ' 石跳桥是黄姚古镇景点之一，整座桥长19m，由31个石墩组成，石墩由两块高矮不同长宽相同（约35㎝）的正方形面青石条组成。高石墩露出水面，较矮的石墩埋于下方，起支撑作用，而31个石墩间距按人行走步伐恰当排列，既利人行，又不影响泄洪。而且由埋在河床下的松木固定在一条直线上，上小下大，既小巧，又牢固。 ', 'zhaoping3067', '', ' 0774-6722748 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (478, '周家水库', '134068', '地址：贺州市昭平县周家水库', '全天开放', '4.1', ' 在往昭平方向约三四公里的地方，乘车电三轮1元，走路也可以。这个水库有20个公用的竹排，可以随意使用，不过一定要注意安全。往水库途中，两边的田园风景很好，该地区特有的黄泥砖墙，配上纵横阡陌，感觉柔美。回程遥望古镇，镇子后面喀斯特地貌群峰耸立，古朴秀美。 ', 'zhaoping3067', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (479, '桂江生态旅游景区', '2493521', '贺州市昭平县，上起文竹镇临江村，下至昭平镇富裕村下福电站', '8:00-18:00', '4', '[桂江生态旅游景区位于贺州市昭平县，上起文竹镇临江村，下至昭平镇富裕村下福电站。全长约25公里，以昭平旅游集散中心为主，,  , 桂江生态旅游景区是国家3A级景区，景区主要浏览节点包含宝塔公园、松林峡、临江新村、翡翠湾半岛。这里江水如青罗玉带，秀美天成，两岸青山黛绿，翠竹掩映，沿途自然景观异彩纷呈，与“高峡出平湖”的壮丽景观融为一体，蔚为壮观。,  , 流淌千年的古航道，从秦始皇开凿灵渠，沟通长江水系与珠江水系，桂江就成为一条黄金水道，溯源千年，无数文人墨客、达官贵人、商贾名士都曾在这条航道上行船，这段姿态万千，让神仙都留恋的生态秘境，不知让多少人为此倾情陶醉，流连忘返……,   ]', 'zhaoping3067', 'AAA景区', '', '');
