INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (481, '郭家大院', '1830690', '地址：贺州市昭平县黄姚古镇西南角（毗邻太平门）', '全天开放', '4.1', ' 郭家大院是黄姚古镇典型的平地庭院式建筑，修建于清朝乾隆年间，院子祖先可是盛唐有名的将领郭子仪。占地面积约20多亩，有接龙门、太平门等四处门楼，门前有池塘和花园，四周有高高的石砌围墙，屋舍近百间。宅第设计巧妙、严谨，所用的砖，每一层都是用一大一小两块砖砌成，层层码靠。每一块砖都经过人工打磨的，据说当年主人建房时，严令砖瓦匠每日只许造三十块青砖，多一块则尽数敲毁，故每一块青砖无不精心打磨，大小、色调深浅如一，至今手敲依然铿锵有声。 ', 'zhaoping3067', '', ' 0774-6722748 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (482, '黄姚石板街', '4245702', '地址：贺州市昭平县黄姚古镇内', '包含于黄姚古镇景区内，景点开放时间详见景区现场公示。', '4.1', ' 黄姚古镇较出名的景观之一就是黄姚石板街，黄姚石板街目前有8条，分别为：金德街、迎秀街、天然街、中兴街、安乐街、连理街、龙畔街、山磅街。迎秀街的石板街最阔，约5米；最窄处为金德街羊巷口，不到两米，两旁各摆肉台一张，行人相对而行，只能擦肩而过，每逢墟期节日，交通为之阻塞。 ', 'zhaoping3067', '', ' 0774-6722748 ', '');
