INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (522, '五皇山', '2024768', '钦州市浦北县龙门镇马兰村五皇山风景区', '全年 09:00-17:00。', '4.2', '[钦州五皇山原名叫“五皇岭”，浦北八景之一，五皇山是钦州市著名的森林公园，位于钦州市浦北县龙门、北通、白石水、大成、张黄五镇境内，距离县城30公里。,  , ,  ]', 'qinzhou697', 'AAAA景区', '', '');
