INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (560, '歌娅思谷', '1984903', '地址：广西南丹县里湖·甘河', '周一-周五8:30-17:30，周六、周日8:30-21:00', '3.9', ' 歌娅思谷景区位于南丹县里湖乡甘河屯，是白裤瑶民俗风情的集中展示区。景区总体规划按“一点、一线、九个区”来建设。一点：洞天古韵--白裤瑶民族文化展演基地； 一线：地下大峡谷--岩溶地质博物馆；九区：特色酒店区、餐饮服务区、农耕体验区、民俗工艺展示区、民族体育竞技区、细话歌园区、生态养殖区、戏水区、农业综合开发区。景区围绕白裤瑶族婚俗、葬礼、服饰、宗教、饮食、陀螺、铜鼓、勤泽格拉等多种兼具浓郁民族特色文化来建设。 ', 'nadan3059', '', ' 0778-7271999 ', 'http://www.ndgysg.com/');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (561, '巴平梯田', '2502862', '地址：广西壮族自治区河池市南丹县芒场镇巴平村', '全天开放', '4.5', ' 巴平梯田，梯田及地形地貌非常有特色，春夏秋冬各不相同，加上南丹独特的白裤瑶民族风情，有着与众不同独特的气势和宏伟的景观。地形别具特色，梯田的时令作物更迭，春夏秋冬景色各不相同。春季，梯田开始蓄水种稻，农民耙田插秧，在蓝天白云的映照下，整个梯田波光粼粼，婉如一面面叠放的镜子，闪光锃亮；夏季，绿油油的水稻郁郁葱葱，随着梯田弯曲起伏的走势跌宕起伏，犹如一块块翡翠，迷人心扉；秋季，满坡遍野的稻谷金黄耀眼，似是整个梯田铺上了一层黄金，绚烂夺目；冬春，油菜花开，绿叶黄花间，蜂飞蝶舞，如画如梦。 ', 'nadan3059', '', '', '');
