INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (576, '边关硕龙地下长城', '1681413', '地址：崇左市大新县硕龙镇沿边公路硕龙派出所旁', '景区暂时停业，具体开放时间详见景区公示。', '3.1', ' 边关硕龙地下长城又名“7812地下防炮工事”，位于中越边防重镇硕龙镇，离该镇硕龙街中心广场100米，与越南只有一条不宽的归春河相隔。它是上世纪70年代在中越关系紧张时修建的地下军事防御工程。该工事深藏于硕龙镇石山地下数米深，与民居、山峰、界河等盘龙贯通，历时数年而成。地道内各种战时设施齐全，具有了望监视、观测通讯、指挥调度、哨兵岗、武器弹药库、军用物资供给、战时地下医院等功能和设施。硕龙边关地下长城全长3公里的“地下长城”像蜘蛛网般密布于硕龙街数十米深的地下，构成了一道坚不可摧的地下钢铁长城。看过电影故事片', 'daxin963', '', '', '');
