INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (578, '明仕田园壮族民居博物园', '3226469', '堪圩乡明仕村', '全年 09:00-17:30；表演时间：10:00-10:30,11:00-11:30,14:00-14:30,15:00-15:30,16:00-16:30,17:00-17:30。', '4.6', '[明仕田园壮族民居博物园位于, 游船下船码头出口处，依托明化田园秀丽山水，集中展示了广西壮族各县特色的民居风情、独特的手工技艺、节庆活动、生活习俗等。乘坐游船下船后免费参观，单独参观博物园需购票。]', 'daxin963', '', '', '');
