INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (653, '千盛农庄', '1412056', '地址：柳州市柳城县沙埔镇', '', '5', ' 农庄中河水清澈，竹木林立，环境优美，早在清朝时期，各地达官贵人时常到这里游玩、避暑，以观山水、看奇石，尝河鱼为荣。农家美食是千盛农庄最大特色之一，它遴选本地新鲜食材烹制而成，原汁原味，风味独特。观景客房，拥有独立卫生间，干净舒适，到这里旅游小住，远离城市喧嚣，令人无比舒心惬意。农庄漂流有3个河段可供大家选择，每个河段河流需要1个多小时，每个河段都有不同的险滩、奇石、怪树、翠竹，是您激情逍遥的最佳选择。 ', 'liucheng3072', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (654, '桂柳田园', '2511742', '柳州市柳城县大埔镇葛麻洞', '全年 11:00-18:00。', ' ', '[桂柳田园主要以果蔬观光采摘为主，采摘区有十几个高科技果蔬大棚，种有辣椒、韭菜、小南瓜、圣女果、食用菌等十几种果蔬。周边还有蜜桔园、油桃园、青花梨和葡萄园，一年四季都有新鲜水果和蔬菜供游客采摘，这里还有玫瑰观赏园。, ,  , ,  ]', 'liucheng3072', '', '', '');
