INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (669, '查比花海呦呦鹿鸣高端葡萄庄园', '4280345', '地址：柳州市鹿寨县G72泉南高速', '6:00-24:00', ' ', ' 查比花海地处呦呦鹿鸣精品葡萄示范区内，观光面积300亩，配套设施齐全有农家乐、烧烤场、儿童游乐园等周边体验，让游客品尝地道的农家饭、鉴赏丰富的民间艺术文化和生态农耕文化、采购绿色生态的农副产品，充分体验怡静的田园生活。马鞭草竞相开放，小小的花瓣随风摇曳，远远望去就像一片紫色的海洋。这些马鞭草高1.2米左右，紫色的花有点像薰衣草，略带有清新怡人的香气，散发着浪漫和神秘的气息。 ', 'luqian3054', '', ' 18778291551 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (670, '摩天轮', '5039377', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', ' 从站台乘吊篮徐徐升起，升到高空，环绕一圈后又回到站台的观光体验，以其明丽变幻、动感迷人的灯饰、别具匠心的现场配景、柔美动听的背景音乐。 ', 'luqian3054', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (671, '冲上云霄', '5039378', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', ' 一字型的垂直极限，在重力的作用，近乎疯狂的速滑，从18米高的塔端开始急弛，让人体验竞技的魅力与悬空落下的快感，滑行速度达到50公里/小时甚至更高，让快乐尽情释放! ', 'luqian3054', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (672, '波浪翻滚', '5040373', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', ' 该项目是一种由平动、扭动和转动三种运动型式合成的体验表现形式。当转臂和摇臂同向转动，乘客轿厢为作空中大回环平动，游客犹如漂泊在汹涌的波涛上，跌宕起伏；当转臂与摇臂反向转动时，乘客轿厢是在空中作扭麻花运动，再加轿厢的无方向自由转动，乘客体验忽上忽下、忽前忽后、连续翻转扭动和巨大离心加速度。 ', 'luqian3054', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (673, '主题双层旋转木马', '5037386', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', ' DW-88A型双层豪华转马是上、下层可单独作旋转运动的装饰豪华的旋转木马，体现了欧洲皇家的古典豪华的装饰风格。 双层转台同边采用装饰花析，天花板、内围板镶嵌着彩色浮雕，蓬顶配有皇冠尖塔直冲云霄，并配以变幻的灯彩，造型装观而华丽。马匹及宫式马车具有丰富多彩的花饰，转盘外围采用左右对称螺旋形固定楼梯直达二楼，扶梯和围栏装饰着古典的纹饰。 ', 'luqian3054', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (674, '360全景天地剧场《神话》', '5039376', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', '    360度全景天地幕剧场项目球型银幕直径可达22米，采用大型高清投影设备，结合各种特效，配合精密控制的大型灯光系统和多通道剧场音响声效。游客乘坐在动感平台上，配合各种俯仰、旋转、倾斜等动作欣赏精彩的影片故事内容，获得前所未有的视听冲击和震撼体验。 ', 'luqian3054', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (675, '海盗船', '5040372', '地址：广西壮族自治区柳州市翠岭路9号', '开放时间：10:00-17:00（周一至周日）', ' ', ' “海盗船”是一种绕水平轴行复摆动的游乐项目。该种游艺机因造型不同而名称各异，造型这中国龙者称“荡龙舟”或“摆动龙舟”；本游艺机因为其外型仿古代海盗船而得名。 “海盗船”是既惊险又刺激的如乐项目。乘客由上客梯上船，坐在座位上将安全保护杆压到合适的位置，“海盗船”起动后从缓慢摆动慢慢地到急速摆动——犹如游客乘船出海遇到大风骇浪，“海盗船”时而冲上浪涛之颠，时而跌落波澜的谷底。惊心动魄，既有趣又刺激，深受游客喜爱。 ', 'luqian3054', '', '', '');
