INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (677, '神仙湖景区', '4582089', '地址：贺州市富川瑶族自治县203省道', '9:00-22:00', '4', ' 神仙湖景区位于富川瑶族自治县福利镇境内，距县城9公里，坐落在具有古文化风情遗风的八百岭村落边，因乡野情趣浓郁、生态原始古朴、自然风光迷人、乡土气息浓郁而闻名。由健身娱乐区、田园风光游览区、摘果尝鲜生态区、民俗文化展示区等四大生态旅游休闲区组成。神仙湖彩灯将瑶族文化特色、现代卡通动漫等元素结合，打造出一个穿越时空的宏伟壮观场面，加上新颖的互动式体验，形、色、光、声、动的完美结合，让您如同身处玄幻电影大片之中！ ', 'fuchuan1446258', ' AA 景区', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (678, '东山村', '1838401', '地址：贺州市富川瑶族自治县朝东镇东山村（朝东镇东南）', '全天开放', '3.7', ' 东山村坐落于富川县北部的朝东镇附近，这里拥有许多保存完好的清末民初建筑，目前还未正式开发，保持了比较好的桂东古民居风貌。 ', 'fuchuan1446258', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (679, '贺州川岩', '2484525', '地址：贺州市富川县城北镇石狮村', '10:00-17:00', '4', ' 潇贺古道川岩的岩洞外翠拥群峦，古木荫蔽，洞内宽敞明亮，空气清新；小溪清流潺潺，鱼游虾戏，石幔石笋巧夺天工，潇贺古道悠悠穿岩而过，秦建古桥遗址仍在，石条石墩历历可见。洞口摩崖石刻，气势雄浑，令人回味。 ', 'fuchuan1446258', '', '', '');
