INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (737, '发现弄岗国家级自然保护区', '1714129', '崇左市龙州县独山路2号', '全年 09:00-22:00。', '4.3', '[弄岗国家级自然保护区景区位于广西龙州县，是我国具有国际意义的陆地生物多样性14个关键地区之一，也是林业部与世界自然基金会共同选定的40个A级保护区之一，区内群峰嵯峨、山弄密集、溪流时隐时现、喀斯特地貌典型，植被为世界上罕见保存最完好的岩溶地区热带季雨林，被称为“中国喀斯特地貌的生物基因库”、“广西生态的醉后一片净土”。,  , 保护内植物种类丰富，现拥有蕨类植物和种子植物172科710属1497种，其中石山苏铁、蚬木、望天村等国家Ⅰ级、Ⅱ级保护植物13种，全年气候怡人，空气湿润，负离子量大于2000个立方，为名副其实的“天然氧吧”，是远离繁忙生活后的放松身心度假佳地，避暑休闲圣地。,  , 根据综考报告，保护区有野生动物22目57科139种，其中属国家一级保护的动物有白头叶猴、黑叶猴、熊猴、云豹、蟒蛇、林麝等六种；属国家二级保护的动物有猕猴、黑熊、穿山甲、大灵猫等21种，是全家出游，品味大自然动物世界奥妙的佳地。,  , , ]', 'longzhou3046', 'AAAA景区', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (738, '龙州起义纪念馆', '2023615', '地址：崇左市龙州县独山大道', '夏令：周二-周日9:00-17:30，17:10停止入场，周一闭馆，冬令：周二-周日9:00-17:00，16:40停止入场，周一闭馆.', '4.1', ' 龙州起义纪念馆位于龙州县独山大道，原名中国红军第八军革命纪念馆，成立于1985年2月1日。2004年8月新馆建成后更名为龙州起义纪念馆。馆内收藏着龙州起义时期和中国红军第八军的革命文物和历史资料。《赤色龙州》专题陈列共分22个部分，展线长680多米，陈列立体文物400多件，平面文物图片、图表800多张，内容丰富翔实，全面反映了龙州起义、红八军反帝、反封建的光辉战斗历程和左江各族人民为夺取民主革命胜利前赴后继、英勇奋斗的伟大业绩。在陈列形式上打破传统的陈列手法，利用文物、图片资料、场景等多种表现形式，提高了陈列的整体效果，是人们接受爱国主义和革命传统教育的重要场所及旅游观光的好地方。 ', 'longzhou3046', '', ' 0771-8812446 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (739, '龙州珍稀植物园', '2006990', '地址：崇左市龙州县彬桥乡（原橡胶所）', '8:00-18:00', '4.4', ' 广西龙州珍稀植物园就位于龙州县城西郊10公里处，珍稀植物园的创办单位是广西农垦局和科技厅管辖下的广西南亚热带农业科学研究所，园内拥有千亩无公害生态茶园、百亩亚热带百果园、标准的烧烤场、垂钓湖、游泳池、现代化乌龙茶加工厂、手工炒茶车间、鸿渐茶艺馆、游客接待中心、热带植物种质资源圃，随处可见珍奇花卉、罕见林木。游人还可以在无公害生态茶园穿民族服装、背茶篓采茶，学习茶叶加工制作过程、手工制茶、品尝百果；让您在植物园内陶冶情操、增长知识、健康体魄，切身领略绿色健康，回归自然给您带来的无限乐趣。 ', 'longzhou3046', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (740, '左江花山游船', '2007007', '地址：崇左市龙州县上金乡紫霞洞码头', '发船时间参考11:30，14:30，具体以景区发船时间为准。', '4.1', ' “运德1号”豪华游船停靠于龙州县上金紫霞洞码头，该游船总吨位226吨，设有贵宾室、茶艺厅、舞台，配备中央空调、KTV音响、液晶电视等设施，服务内容包括水上旅游观光、餐饮、茶艺、天琴风情表演等，是目前左江最大、最豪华、档次最高、最具特色的游船。 ', 'longzhou3046', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (741, '中国红军第八军革命纪念馆', '2575082', '新街19号', '全年 08:30-17:00。', ' ', '[红八军纪念馆自自1985年2月1日（邓小平领导龙州起义、创建红八军纪念日）成立以来，己接待前来瞻仰、参观的观众达100多万人次。 馆内的红八军军部旧址原为当时龙州乃至广西西南地区有名的“瑞丰祥钱庄”，由一幢法式建筑风格的楼房和一幢中式传统建筑组成，是龙州起义前后党的领导机关和红八军军部所在地。1929年末和1930年初，邓小平同志作为中共中央代表先后两次到龙州领导和发动龙州起义，开辟左江革命根据地，创建中国红军第八军，都在这里居住、办公。红八军成立后，在瑞丰祥钱庄设立红八军军部，以指挥广西左江地区的革命斗争和军事行动。瑞丰祥钱庄因此被称为“红军楼”。珍贵馆藏有, 红八军帽徽、, 《工农兵报》, 、, 红八军印刷机等。]', 'longzhou3046', '', '', '');
