INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (806, '罗城剑江风景区', '2494282', '地址：广西壮族自治区河池市罗城县怀群镇栋见屯', '全天开放', '4.8', ' 剑江风景区在距广西壮族自治区河池市罗城县县城35公里的怀群镇剑江一带。这里奇峰突兀，碧水悠悠，处处皆景。剑江十里画廊，“不是桂林，胜似桂林”。 剑江两岸的山奇形怪状，姿态万千，有的山体巨大，给人以雄伟之势，有的顶部尖细，似要冲上云霄，有的像开屏的孔雀，有的似呼啸的雄狮猛虎，有的如同戏水的鸳鸳，有的像忘情的歌仙刘三阻，个个形态逼真，栩栩如生，活灵活现。剑江，涓涓流淌，碧蓝澄清，河底白色鹅卵石历历可数。河中不时露出半爿沙滩，沙质洁白细腻。山，耸立如林，密集重叠，千姿万状，维妙维肖，形神兼备。 山谷里，竹木繁茂，遮天蔽日，清幽雅静，岸边翠竹成荫，古榕参天。坐在凤尾竹的浓荫下四望，河水潺潺，天边白云漫漫，岸上孤烟直直上升，如一幅美丽的图画。高达数十米的才龙瀑布、且兴瀑布、尧河瀑布、肯才瀑布，宛如一条条银河从上天飞落，奔腾咆哮，声震林壑，气势雄伟，奇丽壮观。拉t岩洞，奇诡怪异，五光十色。 在一处峡谷上有一座天然石桥飞架，构成了一座高约50米，宽约30米的拱门，浩洁荡荡，巍巍峨峨。石桥画上古木盘根错节，青翠苍郁。桥下倒挂着无数“胎生值物”，形态万千，其状莫名 ', 'luocheng3050', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (807, '天门山风景区', '1836775', '地址：河池市罗城仫佬族自治县么佬族自治县怀群镇向西3.6公里', '', '4.7', ' 天门风景区位于罗城县怀群镇向西3.6公里处，这里群山抱绿水，景色非常宜人。景区内有自然形成的天生门，名为“穿岩”，岩壁光滑如磨，犹如一扇敞开的巨门。清幽幽的元蒙河从岩下悠悠流出，清澈见底，两岸浓荫的翠竹尽映其中。这里的清凉游、亲水游项目大受青睐，游客可以坐游船从天生门下穿过。 ', 'luocheng3050', '', '', '');
