INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (833, '十里画廊', '3497565', '地址：幼儿园后门', '7:00-18:30，具体开放时间详见景区现场公示。', '4', ' 钟山十里画廊景区位于县城西北18公里，国道323线及即兴建的桂梧高速公路旁，因景区地处公安镇荷塘村而得名，面积约36平方公里。景区内分布泥盆系石灰岩，系下古生代碳酸盐沉积。景区内分布泥盆系石灰岩，系下古生代碳酸盐沉积。风景区内最高点为皇冠山，主峰约海拔450米，最低处为公婆山水库底，地势呈东高西北低，景区内属中亚热带湿润季风区，其特点为光热丰富，雨量充沛，温凉适度，寒暑适宜，夏长冬短，季节分明，夏涝秋旱，雨水不均，春迟秋早。 ', 'zhongshan3070', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (834, '广西省工委历史博物馆', '2485040', '地址：贺州市钟山县清塘镇英家革命老区', '9:00-17:00', '3.6', ' 中共广西省工委历史博物馆是一个反映和展示中共广西省工委革命历史的博物馆，着重反映和展示以钱兴为书记的中共广西省工委（1940年12月恢复重建）在经历桂林“七·九”事变后，从桂林转移到钟山英家时期，领导指挥广西革命的艰苦历程和光辉业绩。陈列内容分四个部分：第一部分——革命历程，第二部分——组织建设和武装斗争，第三部分——历史丰碑，第四部分——历史回顾。包括省工委主要做的四件大事和在这五年中广西各地党组织建设及武装斗争情况，同时展示这时期在广西地下革命斗争中所涌现的革命先烈、革命前辈的革命历程。 ', 'zhongshan3070', '', '', '');
