INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (851, '超级秋千', '5037351', '地址：广西省崇左市扶绥县岜盆乡祥宁路1号', '开放时间：09:00-18:00（周一至周日）', ' ', ' 超级秋千，外形象一个五颜六色的巨型花篮，当花篮运转时，气动装置将挂有座舱的长臂高高撑起，接着又缓缓收落。这时长臂你来我往，我上你下，交替运动。 ', 'furui3068', '', '', '');
