INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (853, '超级大摆锤', '5037352', '地址：广西省崇左市扶绥县岜盆乡祥宁路1号', '开放时间：09:00-18:00（周一至周日）', ' ', ' 大摆锤是一种大型的游乐设备，常见于各大游乐园。游客坐在圆形的座舱中，面向外。通常，大摆锤以压肩作为安全束缚，配以安全带作为二次保险。座舱旋转的同时，悬挂座舱的主轴在电机的驱动下做单摆运动。大摆锤的运行可以使置身其上的游客惊心动魄。大摆锤属于刺激型的游乐设备。 ', 'furui3068', '', '', '');
