INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (914, '水锦顺庄旅游景区', '3458364', '南宁市马山县古零镇乐平村水锦屯', '全年 09:00-17:30。', '4', '[, 古零镇, , , , , , , ,  , 7D全透明动感玻璃桥坐落于广西南宁市马山县水锦顺庄旅游景区大峡谷上，全长268米、垂直悬空108米。该玻璃桥的刺激性不仅是因垂直悬空高度高，还有全息7D音画同步的特效亮点，相比起传统的玻璃吊桥，这座7D动态玻璃桥最大的特点就是科技感十足，每一步都挑战你的视听感官，人在桥上走，会有水流、玻璃裂开的声音，甚至还会出现玻璃碎裂的花纹。不一样的身高，不一样的体重，便会产生不一样的玻璃碎裂花纹。,  , 当下网红全国的泡泡屋酒店，就是在水锦顺庄景区内。它将向你呈现美丽星空，零距离亲近大自然。心宿泡泡屋不是传统的钢筋水泥客房。这个生态友好型酒店由气压控制的泡泡组成，内有全尺寸的床和配套齐全的的浴室，心宿泡泡屋坐落在溪流边，你可以在早晨边欣赏荷花边吃早餐、观看风车，下午戏水游泳，在夜晚看令人难忘的星空，或者调控灯光进入睡眠模式的颜色。,  , , , , , ]', 'mashan1446255', 'AAAA景区', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (915, '小都白村', '1789928', '地址：南宁市马山县古零镇乔老村', '全天开放', '4.1', ' 小都白综合示范村位于马山县古零镇乔老村，是南宁市第一批综合示范村建设村屯。建设后的小都白综合示范村，将打造成“水车之乡”，集度假、美食、观光、休闲旅游于一体的新型农村。其继承了区域浓郁的民族民风传统，突出了挖掘壮、瑶民族特色文化，与基础设施建设同步实施同步融入，建设成了既有现代物质文明，又保留有传统民族文化特色的市级综合示范村。 小都白具有观光农业园、民族风情园、水车园、观景园、健身活动中心，在适宜休闲旅游的小都白综合示范村里，壮、瑶特色民族文化元素随处可寻，村庄建筑主体以灰白为主色调，村庄道路、供电、供水、排水、排污设施齐全，具有优良的现代农村人居环境，形成一个集果疏种植、休闲观光、农家旅游为一体的现代农业生产基地，游客可以在这里采摘果蔬、休闲度假，徜徉于“山水影农家，诗画小都白”如梦如幻意境之中。 ', 'mashan1446255', '', '', '');
