INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (971, '新圩阻击战酒海井红军纪念园', '5676353', '桂林杨门虎将灌阳县新圩镇和睦村下立湾屯北约500米处，全灌公路（省道201）西侧', '09:00-17:00（周一闭馆，节假日除外）。', ' ', '[灌阳新圩阻击战酒海井红军纪念园位于灌阳县新圩镇和睦村下立湾屯北约米处，全灌公路（省道）西侧，是新圩阻击战一百多名红军战士殉难之处。, 年开始，灌阳县委、县政府筹资修建酒海井红军烈士纪念碑及护栏，在酒海井前立“红军烈士殉难处”石碑；年月竣工并向社会开放。年月，被列为国家重点文物保护单位。年月对酒海井原有的红军烈士陵园进行规划扩建，将散葬于民间的红军遗骸归葬到酒海井烈士墓。主要建设设施有纪念公园、烈士墓园、旅游公园，具体项目有纪念广场、红军帽、纪念塔、纪念馆、红军雕塑、烈士英名录等。如今，这里已成为缅怀红军烈士、学习传承红军长征精神的圣地。]', 'guanyang3071', '', '', '');
