INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1020, '允升塔', '57757', '地址：广西梧州市蝶山区火山路', '', '4.5', ' 在梧州市西江南岸锦屏山，清道光三年（1823年）建。据两广总督阮元诗：“云山郁蒸，江水澄凝。得此高塔，势欲上腾。梧岗吉上，从此其兴。”而名为“允升塔”。相传允升塔是吕洞宾为救一对鸳鸯鸟而掷下的一杆笔。塔平面六边形，底边长3．9米，七层楼阁式砖塔，通高36米，塔身叠涩出檐。底层以及三至七层正面各开券顶门，二层各面相间开长方形、圆形窗，其余各层相间、上下相错开5个方形窗。塔顶六角攒尖。塔内各层设木楼板和木楼梯。门额有阮元题刻的“秀发梧江”、“观文成化”、“光射牛斗”等石匾。1982年维修，改木楼梯为铁楼梯。 ', 'wuzhou142', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1021, '白鹤山', '57750', '地址：鸳鸯江畔珠山旁', '8:30-18:00', '3.9', ' 白鹤山位于畔珠山旁。白鹤山为梧州八景之一，昔称“鹤冈返照”。当朝阳刚从西江浮起，河上晨雾未消，隔江相望，窈窕多姿的白鹤山，宛如一个静卧云雾中的仙女。当斜阳西照，霞光把白鹤山的倒影投入碧绿明亮的桂江，就象一个花容月貌的女郎对镜梳妆。清光绪二十三年（1897年）梧州辟为通商口岸后，英国在白鹤山四周竖起大英国地界碑，建起领事署。惠师礼会、圣书公会、美国圣经学院也设在这里。白鹤山下立有禁止游人登山的木牌。1918年春，有桂军数人登山游玩，英领事放出狼狗咬人，桂军排长为自卫而打死洋狗，后被英领事署威逼而遭枪杀，以偿狗命。经过反英浪潮后，英国于1928年交回白鹤山。后来白鹤山上建立了。 ', 'wuzhou142', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1022, '八宝塘', '57759', '地址：广西梧州市蝶山区', '', '4.7', ' 位于浔江南岸山中，有湖湾、小岛和小山，丛林密布，相传以前八仙曾云游至此，鼓农仙乐玩赏通宵，后留下八个小宝洲作纪念，故称“八宝搪”。因北回归线横贯八宝塘而过，开辟为天文公园。 ', 'wuzhou142', ' ', '', '');
