INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1026, '大化红水河百里画廊', '69740', '河池市大化瑶族自治县码头路42号', '全年 周一至周五 09:30-09:45，周六至周日 09:30-09:45；周末加开15:00船次，需14:45前到达使用，游玩时长100分钟。', '4.3', '大化红水河百里画廊景区是大化水电站至江南乡的红水河段，长50公里,方圆200多平方公里。大化水电站闸坝建成后，红水河河道形成了宽300-500米，深30-50米的峡谷水库，河水清幽，两岸群峰屏立、竹木葱葱。 此段红水河可谓是一时一景，一辰一季，一山一水，一草一木皆成格调。云烟里，山点粉黛，水施浅妆，犹如西施抱恙，斜倚阑干，秀发逶迤，放眼处，美人脉脉，云烟成雨；初晴时，碧空如洗，闲云随风，飘飘乎若策马仙人，御气而游，太液池浅，玉碗斜倾。山不据险独占其秀，水不厌深独得其幽，拖曳百里，繁花泻树，炊烟尽头，群芳斗艳，故称之“百里画廊”。 碧波炊烟画卷里，轻舟已过山万重。红水河百里画廊景区新添了两艘游船，“长寿8”和“长寿9”，“长寿8”航行于大化瑶族自治县大化水电站至情人湾河段，游船长37.65米，宽8.2米，可容纳145位游客。游船分主甲板和观光甲板，观光甲板是乘客露天休闲观光欣赏美景的绝佳地，视野开阔，凭栏远望，青山屏立，流水清幽，船移景换，姿色万千。除了传统的游船观光外，船上还有民族歌舞表演以及布努瑶特色簸箕宴，观两岸风光，耳边丝竹萦绕，一曲舞罢，美食佳肴尽在眼前，人生一乐事也。 ', 'hechi838', '', '', '');
