INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1034, '炳蔚塔', '57762', '地址：广西梧州市长洲区', '', '3.8', ' 炳蔚塔位于西江南岸的铁顶角山巅，建于清道光四年（1824年），青砖结构，塔身呈六角形，高7层，34米。每层塔檐筑莲花图案浮雕，十分壮观。首层有清代梧州知事袁渭栓刻的“文峦耸秀”，二层碑额“炳蔚塔”（已遗失）3字，为清代状元广西运使陈继昌书刻。 ', 'wuzhou142', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1035, '火山夕焰', '57763', '地址：广西梧州市蝶山区', '', '5', ' 在梧州市南，隔浔江（西江）耸立。周围四季郁葱，因有“埋剑吐芒宵烛汉，藏珠浮映夜辉山”而得名。相传秦末南越王赵伦藏剑于此山，故夜里腾光，每三五天一见，一更初起火，由下蔓延至顶，全山通红，火光冲天，顷刻间而熄灭，翌日山上依旧苍翠欲滴。又传山下有潭，水深无极，下有宝珠，初更浮起，月星皎洁，宝光烛天，如峨眉格迦、南岳圣灯之状，便出现“火山夕焰”奇观。山上产荔枝，每年四月即成熟，曾是盛名全国的“火山荔枝”。山下江边的丙穴，产嘉鱼，状如小鳍鱼，味极美，春末尤佳。据说全国著名之嘉鱼，始产于此，后繁殖各地。唐代诗人沈诠期有“身经火山热，颜入瘴江销”的诗句。 ', 'wuzhou142', ' ', '', '');
