INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1051, '轰天雷', '5042343', '地址：桂林市兴安县志玲路乐满地景区内', '9:00-17:00', ' ', ' 轰天雷是美国西部区一道亮丽的风景线。它每次可乘坐16名游客，高达30米。当您稳坐坐椅，巨大的拉力瞬间让您升上30米的高空，仿佛是轰天雷爆炸时的巨大热浪将您掀上云霄，还未来得及反应，它又以风驰电掣的速度从高空垂直下落，一次次极限的失重的体验，让您在尖叫声中感受在云霄中来回跳跃的惊险与刺激。 ', 'xingan722', ' ', '', '');
