INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1059, '历史文化长廊', '57780', '地址：广西梧州市万秀区西江路', '', '3', ' 梧州历史文化长廊利用长达1.5公里的梧州市河东城区桂汇段防洪堤内壁制作，从鸳江桥底西桂江段防洪堤转角处至桂北闸口处。共有17幅大型浮雕、6个文化景观、3个水景、55幅雕刻诗词书法。表现的内容分为梧州龙文化、古文化、传说文化及近代文化，刻画了梧州的历史沧桑，展现了独特的民俗风情，表现了梧州人的奋发精神，具有极高的历史文化价值和艺术欣赏价值。同时，其长度和大面积的浮雕群在全国极为少见，嵌于防洪堤壁上的五龙阴雕更是绝无仅有。通过游览梧州历史文化长廊便可一日读懂梧州上下4000多年文明历史。 ', 'wuzhou142', ' ', '', '');
