INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1082, '如意岛', '2481281', '崇左市江州区新和镇如意岛', '全年 09:00-18:00。', '4.3', '如意岛，坐落在江州区西北30公里处的新和镇，由6公里天然水域湖泊组成，整个景区的航拍俯瞰，形似中国传统吉祥物——玉如意伏卧于一湾江水，故而得名，寓意走进如意岛，三生三世都会吉祥如意。 整个景区，典型的喀斯特内陆山脉绵延起伏，湖区内石林千姿百态，曲径通幽，无论哪里皆是一幅浑然天成的山水画。静静的黑水河从此处流过，偶尔还能看到野鸭在湖中嬉戏，白鹭蹁跹。如意岛的一树一山一水一景皆是原生态的湖光山色，不仅风光一绝，环境亦是杠杠的。何以为证？看那时而恣意翱翔于空，时而贴水而飞，时而栖于树梢上的白鹭群就知道了。能得到对环境要求“眼高于顶”的白鹭的青睐，如意岛的生态环境如何，不言而喻。 ', 'chongzuo837', '', '', '');
