INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1106, '古龙河景区', '3438299', '河池市宜州区古龙河景区(祥贝乡古龙至下枧龙洲岛)', '全年 09:00-16:55。', '5', '古龙河漂流景区位于江河中段，北起祥贝乡古龙圩，南至下枧龙洲岛，古称思吾溪，全程10公里。古龙圩后山有古龙洞，洞口宽7米，高5米，有钟乳一丛酷似古榕挺立。洞内有数个大厅，有一通天洞。全洞长数千米，景观奇幻。 沿河撑漂流，河岸有皇冠山、义马柱、古佛朝西、龙女岩、鸳鸯洞、六妹岩等景点。龙女岩一年四季清洌泉水缓缓淌出，洞口宽敞，高15余米，宽20米，泛筏进入10余米处而处，可观泉眼。严冬季节，此泉暖气袭人，常有阵阵雾气逸出，当地村民常到此洗浴。相传古时这里曾有身着洁白纱衣的龙女出现，彩调剧《龙女与汉鹏》的故事传说就由此而来。顺流而下，漂下金波跃鲤坝滩100余米，即可穿入鸳鸯洞，洞深处有天然瀑布，时有游鱼嬉戏。 ', 'hechi838', '', '', '');
