INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1139, '荷美覃塘景区', '5639342', '贵港市覃塘区西北的姚山村、龙凤村', '全年 08:00-20:00。', ' ', '荷美覃塘景区位于覃塘区西北的姚山村、龙凤村，是以具有一千多年种植历史的覃塘莲藕为主导产业，种植莲藕多亩，荷花品种多种。 荷美覃塘景区以特色种养加工业和生态休闲观光为核心，打造成集现代农业和休闲旅游为一体的新型旅游景区，建成以荷花为主要景点、集花卉、荷花精品园、民宿等配套娱乐设施的休闲养生、荷文化旅游、观光为主的景区，打造荷美覃塘的特色。目前，已建成了游客中心、生态停车场、珍荷苑、藕遇荷田精品园、风雨桥、醉莲阁、观赏步道、自行车骑行、文化长廊、陆绩码头、莲心广场、荷家坞广场、荷花科普馆、儿童娱乐中心、荷美覃塘农家客栈、水磨坊农家乐等基础设施。 ', 'guigang711', '', '', '');
