INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1147, '《临贺长歌》演出', '1966695', '地址：贺州市昭平县黄姚镇黄姚大剧院（黄姚古镇内吴氏宗祠旁）', '《临贺长歌》演出暂停营业，具体开放时间详见景区公示，原演出时间16:00-17:00、20:00-21:00。', '4.7', ' 大型古装歌舞剧《临贺长歌》，根据《隋书•列女传》史实改编。讲述南陈末期隋朝初期发生在临贺郡潇贺古道上维护民族团结、国家统一的忠烈爱国故事：岭南酋帅钟士雄在潇贺古道一带，把握重兵，忠君爱民，保护一方平安效忠朝廷；而临贺郡东山垌垌主陈文豪（原南朝宋临庆国王刘氏后裔）隐姓埋名十多年想分裂疆土，自立为王，故设计两道道假圣旨逼迫钟士雄一起反叛朝廷；但酋帅钟士雄聪明睿智，在母亲蒋氏和未婚妻潘蝶花的帮助下，一举消灭了叛贼陈文豪，维护了国家统一。 ', 'zhaoping3067', ' ', ' 0774-6722748,0774-6722758 ', '');
