INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1152, '自驾车旅游营地', '57708', '地址：广西梧州市火山下', '', '4.3', ' 广西梧州市（两广省界）自驾车旅游营地是广西首批省界自驾车旅游接待中心之一，位于广西梧州市风景优美的南岸火山下，这里山清水秀，空气清新，依山傍水、草地成片。夜幕降临还可以遥江眺望美丽的梧州夜景。营地交通方便，位于321国道旁，距离梧州市区仅2.5公里（10分钟车程），距广州约3.5小时车程，为省界旅游和自驾车旅游宿营及中途休憩的首选之地。营地是按照“生态•运动”的概念设计，占地480亩，其中草地面积450亩，配套标准草地足球场24个，草地高尔夫练习场1个20000m2，室内篮球场4个，沙滩排球场4个，沙滩足球场2个，室内羽毛球馆1个（9个场），室内乒乓球馆1个（10张乒乓球台、桌球台6张），沙地网球场2个，游泳池1个150m2，桑拿美容馆1个，150～200m2会议室各1间，以及其它休闲娱乐健身活动设施一批。营地有按星级酒店标准建设的运动员公寓和教练员公寓180间标准客房，房间宽敞舒适，配套完备。营地餐厅可同时容纳300人就餐。聘请梧州名厨制作梧州特色美味佳肴，有正宗梧州纸包鸡，正宗梧州龟苓膏等等地方特色食品。购物中心品种丰富，囊括了梧州各种特产， ', 'wuzhou142', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1153, '六堡茶园', '57709', '地址：广西梧州市苍梧县六堡镇', '', '4.3', ' 六堡茶园位于苍梧县六堡镇，距梧州市区仅30公里。景区交通方便，占地面积2000亩，茶园面积1100亩。茶园各种旅游接待基础设施基本齐全，配有兼职的旅游管理人员，管理规范，服务优质。茶园现有固定职工50人，临时采茶工150余人，主要以茶叶种植、加工、营销、六堡茶文化及生态农业和旅游观光为经营思路。茶园文化底蕴深厚，茶园建筑风格独特，设有茶膳餐厅、六堡茶产品和文化、传统茶叶加工设备展示厅、现场茶叶摘采、加工展示、茶艺表演、露天烧烤等。 ', 'wuzhou142', ' ', '', '');
