INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1165, '圣文园', '57716', '地址：广西梧州市蝶山区', '', '4.5', ' 圣文园坐落在梧州市河东城区西江南岸，从云龙大桥南桥头经旺步村往抚冲4公里即达景区。园内山清水秀，古木参天，北回归线穿园而过。这种独特的气候使得景区内植物品种十分丰富：有冠为“植物活化石”之称、最远古侏罗纪时代便出现的蕨科植物群，有世界十大名木之一的格木，有三十余米高，几人环抱的稀有大树品种。圣文园主要景点有：有华南第一树之称的五子登科树，树龄至少超过200多年、树高32米、同根5枝，如同一父五子，种植此树之后，连续5代人均高中状元之位，为纪念故将此树此景命为“五子登科”。画廊：梧州历代信奉孔子，画廊取用中国最好的青石精心雕刻而成，全长80余米，用图文的形式记载着孔子的一生事迹，这在梧州是罕有的，也是最齐全地记录了孔子这位被尊为“万世师表”的圣人生平事迹的地方。鸳鸯湖：由一大一小，一深一浅，一高一低两个湖泊组成。烧烤场：可同时容纳千余人。景区充分利用北回归线穿园的独特地理条件和气候优势，规划小型天文台，合理地利用了原有的丰富植物资源，划出2000余亩土地山体开辟珍稀树种园、茶花园、桃花园、兰花园等景点，将原有的野生格木（国家珍稀濒危植物、国家二级保 ', 'wuzhou142', ' ', '', '');
