INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1167, '东华寺', '57717', '地址：广西梧州市长洲区长洲镇龙平村', '', ' ', ' 东华寺，位于梧州市区12公里左右的长洲镇龙平村大宕组东华山半山腰。据传，明崇祯年间张氏始创庙庵并置业供给。当时住持僧募化铜以铸佛像，适逢一采樵老妇给其布施一文铜钱，僧嫌其少而亵弃于池塘。在冶炼时炉中之铜跳跃而不熔，僧悔悟，跳下池塘搜回所弃之币，投入炉中，佛像才顺利铸成。同治年间佛像左肩上宛然有铜钱形藏于衣袖间。 ', 'wuzhou142', ' ', '', '');
