INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1283, '仙岛公园', '1729224', '地址：钦州市钦南区龟岛', '9:00-17:00', '4.3', ' 仙岛公园又称逸仙公园，位于钦州港龙门群岛七十二泾景区入口处的龟岛上，因形似一只缓缓爬向海面的乌龟被称为“龟岛”，登岛远眺，钦州港区尽收眼底。 公园与钦州港中心广场遥相呼应，配套建成了环岛路、游乐码头、风轮台、金鼎坛、聚英台。 ', 'qinzhou697', ' ', '', '');
