INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1285, '怀远古镇', '2505797', '河池市宜州区怀远镇', '全天开放', '3.6', '怀远古镇位于宜州市北部，这里历史悠久，可追溯到唐朝时期。这里拥有秀美山水，还保留有众多历史遗迹和淳朴的民风。漫步在怀远古镇内，未经翻修和粉饰的骑楼街展现了这里沧桑与悠久的历史；五大会馆、八大码头的建筑遗迹仍依稀可辨。', 'hechi838', '', '', '');
