INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1493, '大明山万古茶园', '4693341', '地址：南宁市上林县488县道', '10:00-22:00', '5', ' 大明山万古茶园景区是国家AAA级旅游景区，隶属于广西农垦国有大明山农场。景区位于上林县明亮镇万古村境内，距上林县城12公里，距南宁市78公里，有9路公交车直达上林县城，交通十分便利。景区属缓坡丘陵地带，最高海拔为175米。景区属南亚热带季风季候，年平均气温在18℃，是人体最适宜的居住环境，且负氧离子含量丰富，高达30000个/cm³，是我国南方理想的避暑胜地。景区主要由迎宾区、休闲体验区、观赏游乐区、茶叶生产区、食宿区组成，内含文化展示中心、古茶文化长廊、茶农村寨、茶园观光区、万古茶壶、禅茶一味、竹园等主要旅游景点。景区内还设有采茶体验、手工炒茶体验、自助烧烤、自行车骑行、康体游乐场、儿童游乐场等体验、游玩项目。园内茶山连绵起伏，水流环绕，自然风光优美秀丽，空气清新怡人，生态环境极佳，是一个集茶叶生态观光、茶文化体验、休闲娱乐、餐饮住宿于一体的旅游度假圣地。来到万古茶园景区，你可以当一回采茶女，扮演一次采茶郎，亲自体验采茶、炒茶、泡茶及品茶的乐趣。你还可以顺着茶园道路曲折前进，拐个弯，又是一番景象，让您体会一下“山穷水尽疑无路，柳暗花明又一村”的 ', 'shanglin120117', ' ', ' 0771-5285931 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1494, '丹炉山景区', '4359342', '河池市南丹县吾隘镇昌里村丹炉屯', '全年 09:00-17:00。', '4.6', '丹炉山景区内的悬空7D玻璃吊桥通过各项安全测试，已于2018年12月30日正式对游客开放。丹炉山天之道玻璃桥，是目前华南一条7D玻璃吊桥，跨度318米、垂直高度298米，桥面宽度2米！采用目前国内先进的玻璃技术建造而成，可同时承载2100人（按75kg成人标准体重计算）！玻璃的透光率99%，游客全程在云间穿越，无限风光在足下，乱云飞渡仍从容，可谓“凌云飞渡”。丹炉山上完整保存了明代那地土司在此修道炼丹的遗址，丹炉山为东西走向，海拔750米左右，四面悬崖峭壁，林荫谷深、植被丰富、水源不竭，四季风景如画，负氧离子含量极高，是一座天然氧吧，亦是修道炼丹的上佳选址。 走在180米的高空之上，脚下是蜿蜒的山路和过往的车辆，陡峭的山壁和摇曳的枝叶，是一种什么样的感受，赶快亲自来体验一下吧！ 古老神秘的炼丹古遗址,触摸历史斑驳，感悟世事浮沉,历经几百年风雨洗礼的炼丹炉，早已没有了当初的模样，只留下令人无限遐想的遗址。 精彩欢腾的特色演绎,结合壮族文化、丹炉文化等内容，打造系列热闹欢腾的民俗表演。 ', 'hechi838', '', '', '');
