INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1540, '鸳鸯江', '131762', '地址：广西梧州市河西岸', '', '4.2', ' 鸳鸯江是由桂江与浔江汇流而成。碧绿的桂江与混黄的浔江交汇成黄绿分明的鸳鸯江。每逢春夏之际，清浊尤为分明，堪为天下奇观。宋代诗人苏东坡赞鸳鸯江曰：“鸳鸯秀水世无双”，从此鸳鸯江名闻天下。放眼望去，只见浔江浩淼，水流滔滔；桂江碧绿、波光粼粼。两水交汇处，一浊一清、一急一缓，泾渭分明，恰似戏水鸳鸯，相互依偎，相互拥抱，长相厮守，难舍难分。鸯江景色秀美。相传宋代大诗人苏东坡当年获赦路经梧州，适逢中秋之夜，泛舟于鸳鸯江，面对清浊江水不禁思绪万千，吟诗赞道：\"我爱清流频击楫，鸳鸯秀水世无双。\"鸳鸯江水之名也从民间传开来。\"鸳江春泛\"被列为梧州八景之首。凡到梧州的游客游览了这一景色无不赞叹称奇。84年底，梧州市在河西岸的三角嘴处开辟了一个称为“鸳江春泛”的风景区，古朴典雅的凉亭和高低起伏、迂回曲折的沿岸长廊，吸引了我就来不少游客凭栏远眺，目送滔滔东去的大江，观赏清浊汇成的奇观、以及南岸火山上的允升古塔。2000年6月，鸳江汇合处又架起了鸳江大桥，更是为观光的游客提供了一大方便。 ', 'wuzhou142', ' ', '', '');
