INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1584, '川洞河燕子湖景区', '1475891', '地址：河池市天峨县', '8:00-18:00', '4.3', ' 川洞河燕子湖景区是一个远离城市喧嚣的世外桃园，该景区是集天坑、溶洞、峡谷、河流、沙滩、湖泊、原始森林等众多奇特景观为一体的生态景区，。景区内河谷长15公里，远古幽深，河流蜿蜒曲折，河水清澈见底，如黛的原林古木参天。最为神奇的是大自然的鬼斧神工把阻隔河流的大石山凿成一宽30多米，高120多米，长近400米的巨型溶洞，数十万只燕子栖身其间，形成蔚为壮观的绝景，燕子湖的名称也由此而来。古朴的川洞河自莽莽原始森林奔流而来，历经亿万年的天然造化，洞穿大山，注入燕子湖，贯通龙滩天湖；景区内青山、碧水、秀湖、奇洞相映成趣，层峦叠嶂、林海无边，堪称人间仙境；每当春暖花开，引来数十万只燕子在洞内衔泥垒巢，神奇壮观。 ', 'hechi838', ' ', '', '');
