INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1639, '巴马长寿岛', '1830051', '河池市巴马瑶族自治县长寿岛内', '全年 08:30-19:50；日场：8:30-18:00，篝火晚会：19:00-19:50，歌舞表演：9:30,11:30,13:10。', '3.8', '1、中午上岛，13: 10民族舞演出；主持人开场白开场舞（瑶族铜鼓舞）杂技表演瑶家婚礼习俗体验瑶家绝技上刀山舞蹈走在山水间2、下午上岛，17:00开始迎宾；3、17:50--18:50长桌宴用餐（瑶家簸箕长桌宴），18点开席瑶歌敬酒）；19:00-19:50篝火晚会：(注：篝火晚会每晚都有，除有特殊情况提前告知取消）五禽戏（互动、健身讲堂）热舞舞蹈（互动）主持人开场白开场舞：瑶族铜鼓舞杂技表演竹竿舞（舞蹈欢迎你到寿乡来）（走在山水间）长寿老人表演瑶家婚礼习俗体验（抛绣球）瑶家绝技上刀山或喷火点火、篝火舞5、20:00-21 :00梦·巴马大型山水实景演出。以上游玩时间及行程安排仅供参考，具体以景区现场安排为准。长寿岛有云鹤牌坊、太极广场，可远眺奇崖自然形成的人面肖像，惟妙惟肖，可访寿星老人，看香猪赛跑，岛上还可欣赏到参与性极浓的小型原生态瑶族风情歌舞表演。最妙的是：岛上有180米长的【中华养生文化长廊】，展示中国上下五千年的养生论述和巴马的长寿文化。 特别赠送一堂养生大师亲临讲解养生之道，在长寿岛上不仅可以欣赏到美丽的风景，而且晚餐特意安排了享用巴马当地特色簸箕宴，参加岛上的篝火晚会。 让每个游客在长寿岛上观看到民俗走刀山、引火烧身，瑶族婚礼等风情演出，享受在景区的自然环境里，尽情参与狂欢、放松，渡过一个激情、浪漫、洒脱，自我欢乐、自我陶醉的温馨之夜……满足旅游者在旅途中渴望收获真正的户外休闲娱乐，领略景区的民族特色—长寿乡的世外桃源。 ', 'hechi838', '', '', '');
