INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1897, '袖珍国景区', '141686', '地址：桂林市阳朔县十里画廊', '8:00-18:00', '4.2', ' 桂林阳朔县“袖珍国”乐园占地41亩，位于著名景区阳朔十里画廊的北段，园区现有48名袖珍人，年纪最大的51岁，身高最高的只有1.37m，袖珍人来自全国各地，在这安居乐业，结婚生子，创造着属于自己精彩人生。现在的景区开辟了袖珍国机构大院、王宫、铁臂飞车、歌舞广场等10个景点。利用卡通的蘑菇城堡，惊险的特技表演，精心编排的小矮人歌舞、“小矮人”导游公主和城堡卫队等独特形势把各景点串联起来，打造一个梦幻童话的袖珍世界。 ', 'guilin28', ' ', ' 0773-8885072 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1898, '归义县旧址', '50090', '地址：桂林市阳朔县旧县村', '', '4', ' 唐朝武德四年（公元621年），在遇龙河畔的仙桂村边设置归义县，贞观元年（公元627年）废止。遗址旁仙桂村（现为旧县村）为阳朔名村之一，历代达官显贵层出不穷，现村中仍保存有部分古建筑，城墙残垣、归义古井等值得一看。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1899, '中国漓江山水剧场', '4619104', '地址：田园路1号印象刘三姐附近', '漓江山水剧场只在演出期间开放；《印象刘三姐》演出19:30-20:40（演出根据客流量会增加场次，具体以当日现场时间为准）。', '4.4', ' 刘三姐是广西壮族民间传说中的歌仙，该剧场是世界上最大的山水实景剧场。 《印象·刘三姐》是大型桂林山水实景演出《印象·刘三姐》是中国·山水剧场之核心工程。全国第一部“山水实景演出”。 ', 'guilin28', ' ', ' 0773-8811983 ', '');
