INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1902, '大岭山桃园', '26735', '地址：桂林市恭城县西岭乡大岭山村', '8：30—18：30。', '4', ' 平安乡大岭山桃园位于恭城西岭乡大岭山屯，是当地政府充分利用3月桃花绽放的契机，开展的生态旅游观光项目。阳春三月，大岒山桃园－漓江畔上的中国桃花艺博园，十里桃花，百里画廊，万亩桃花相映红；金秋时节，水果飘香，果不醉客客自醉。有山歌唱道：“来到恭城逛果园，果子碰头又碰肩。万亩果园耍一转，不愿天上做神仙。”这时，漫山遍野盛开的桃花，染红了大岭山，每到当季都吸引着各方游客前来观光。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1903, '会仙湿地', '1417914', '地址：桂林市临桂县会仙镇睦洞村七星村82-1号', '', '4.6', ' 会仙湿地主要分布在临桂县会仙镇的睦洞、四益、新民、山尾、文全、马面等6个村委以及四塘乡的大湾村委、雁山区的竹园村委，涉及47个自然村。其地域范围内有相思江、良丰河、唐代开凿的桂柳运河（又称相思埭运河）及睦洞湖等河流湖泊。临桂县会仙湿地公园管理局正式挂牌，将履行管理、开发、保护会仙湿地的各项工作职能。可以在七星村码头乘船游览。 ', 'guilin28', ' ', '', 'http://www.huixianshidi.com/');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1904, '中禄寨', '1417564', '地址：桂林市龙胜各族自治县和平乡龙脊梯田景区内', '全天开放', '4', ' 中禄寨坐落在平安和大寨之间。中禄寨的民居很分散，有的在山腰，有的在山谷，都散落在这个山窝里。 ', 'guilin28', ' ', ' 773-7583188；0773-7583088 ', 'http://www.txljw.com/');
