INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1931, '大印人文景观园', '1678868', '地址：桂林市阳朔十里画廊内工农桥边', '09:00-17:00。', '4.7', ' 大印人文景观园是国家AAAA级景区园，坐落于十里画廊的黄金地段遇龙河畔。景区致力于打造养生长寿等文化主题，拥有五项上海大世界吉尼斯之最、国宝级书画展厅、四星级生态度假酒店和环保养生自净化循环水系统，同时也是桂林两江炎黄书画院所在地，再现了古“寿阳城”美誉的综合型园林景区。世界上唯一一家拥有七项上海大世界吉尼斯之最的景区。 ', 'guilin28', ' ', '', '');
