INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1980, '红军岩', '131722', '地址：桂林市龙胜县城东部', '', '3.9', ' 红军岩位于距龙胜温泉核心区4公里的白面红瑶寨下面，形状像巨龙伸出的舌头，这块奇特的岩石犹如横空出世，人们比喻为“遨游的巨鲨”、“飞翔的机翅”、“横刺的巨剑”，岩下有一小坪可容一、二十人聚会，大自然的神工和伟大，令人惊叹。如今，白面红瑶寨已成为龙胜瑶族民俗风情旅游村，红军岩和屹立在它一侧的红军纪念亭成为白面瑶寨的门户。 ', 'guilin28', ' ', '', '');
