INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1985, '漓江（冠岩-杨堤）游船', '5639369', '地址：桂林市雁山区草坪回族自治乡桂林漓江冠岩景区', '10:00-15:30', '5', ' 漓江（冠岩-杨堤）游船可欣赏漓江两岸的自然美景，杨堤乡-草坪段约20公里的水程，大约游历了如下自然景观，这里不妨作以简要介绍。游船驶离码头大约半小时，抵达了黄牛峡，这带景观狭上多奇石，壮如碧莲、狮虎，更因如黄牛群而得名。漓江水到此急转90度大弯，民谣谓“九牛对三洲，河水两边流。五马拦江过，双狮滚绣球”。船过黄牛峡后，在漓江西岸即见望夫石，山巅之上有一石如背着婴儿凝望远方的妇女，故名“望夫石”。船再行10余分钟后，便到了“半边渡”，由于两个渡口均位于同一岸边，因而当地人称之为“半边渡”。这里石壁险峻，峰峦如朵朵出水芙蓉，正是“此地江山成一绝，削壁成河渡半边”。当船驶离桂林市区46千米处，呈现在游人眼帘的两个山峰像一对倒挂的羊蹄，恰巧此山后有一个叫杨堤村，故取谐音称此山峰为杨堤。杨堤是漓江枯水期游船的起始码头，也是当今盛行徒步游漓江的起点或终点。 ', 'guilin28', ' ', '', '');
