INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1988, '桂林千家洞景区', '131533', '地址：桂林市灌阳苏东村', '7:00-18:00', '4', ' 千家景区位于桂林市灌阳县苏东村，距离桂林市150公里。景区依托千家洞自然保护区的原生态景观和世界瑶族发祥地的深厚文化底蕴，以瑶族建筑、特色服饰等民俗文化遗产为彰显形式，构筑历史文化传统与自然山水田园相融合的世外桃源。 到桂林旅游，一般人们只知道漓江、阳朔、龙胜梯田等景点，殊不知在桂林的东北部，还有一个风景及人文历史都相当独特的千家峒景区。这里原生态景观和世界瑶族发祥地的深厚文化底蕴，构筑成历史文化传统与自然山水田园相融合的世外桃源，让人印象深刻，千家峒是瑶族先祖聚居生活的地方，曾居住着千户瑶族人家。     ', 'guilin28', ' ', '', '');
