INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1991, '莫一奇峰', '4960341', '地址：桂林市阳朔县葡萄镇321国道西100米', '8:00-17:00', '3.5', ' 莫一奇峰位于阳朔葡萄镇桂阳公路321国道旁，是一个以壮族土司文化人文景观民俗风情主题园及旅游演艺为主要游览点的景区。莫一奇峰绿荫环抱，林园雅致，涧泉谜延，自然景色婉约清新；七壶香茶文化朴素纯真；传说中莫一大王的亦古亦今主题园、莫土司“画疆墨守”古墙、“莫土司衙门”、“十二神兽园”、“古龙灶、“练兵场”、“点将台””等文化遗迹涵蕴着厚重和神秘。大型情景剧妙趣横生的演绎着传颂千年的《莫氏春秋》情景剧。 ', 'guilin28', ' ', ' 0773-2351388 ', '');
