INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (1998, '好哒呀野趣农场（营地）', '5381346', '桂林市阳朔县兴坪大头山田嵅内', '全年 09:00-17:30。', '3.5', '阳朔.好哒呀野趣农场（营地）位于阳朔县兴坪古镇国家级漓江风景名胜区内，营地总占地面积600多亩，是集“原乡体验+营地教育+野宿美食”于一体的原乡体验野趣农场，重在农耕文化体验及参与互动，把游客变成演员而非观众，沉浸式体验，再辅以科普教育，户外运动及美宿美食，最终打造传统文化体验综合型营地。 包含项目：皮划艇（短艇/长艇），休闲垂钓，撑竹筏，捉鱼摸虾，动物喂养，骑马体验，手工DIY，CS野战，登山探洞，田园射箭，果蔬采摘等等，具体以农场公示为准。 园内体验项目', 'guilin28', '', '', '');
