INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2000, '明心寺', '1418091', '地址：桂林市灵川县海洋乡东北2.5公里处的海洋山下', '', '4', ' 明心寺位于桂林灵川海洋乡东北方向的海洋山下。该寺始建于唐代，宋代赐名灵泽庙，明朝更名龙母庙，清称海阳庙，后改称明心寺，以祭祀湘漓二水之源神。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2001, '玉圭园凯旋王国陆地乐园', '1731782', '地址：桂林市雁山区良丰路（环城高速公路口）', '全年 不开放。', '4.3', ' 玉圭园凯旋王国陆地乐园是桂林新开园的陆地主题乐园，乐园主要由先进安全的大型机动游乐设施、趣味景观、主题游乐、精彩表演、特色美食、旅游商业组成。在80多亩的乐园之中，有20种惊险刺激的游戏项目供游客游玩，超大型悬挂过山车、矿山车、激流勇进、大摆锤、海盗船等，惊险刺激。其中如梦幻快车、矿山车等超大型游乐设施也是首次引入广西。除了游戏设备，园区内的建筑也非常有特色，宛如童话世界里的殿堂、城堡，形成了独具魅力的梦幻风景。此外，主题乐园还专门配套了很多针对低龄儿童的娱乐项目，如大型室内的儿童城堡、翻斗乐、碰碰车、儿童剧场等，适合各年龄层次的游客游玩。 ', 'guilin28', ' ', ' 0773-3190666 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2002, '樱花岛生态乐园', '2023643', '地址：桂林市七星区朝阳乡丫吉村委司公村84号', '9:00-18:00。', '5', ' 樱花岛生态园坐座落于七星区朝阳乡司公村，主要有花卉观赏，苗木种植，休闲采摘，农家土味，烧烤钓鱼，团体拓展等。樱花花色幽香艳丽烂漫，为早春3月开花观花，盛开时节花繁艳丽，满树烂漫，如云似霞，极为壮观。种在樱花岛的每个角落，给人不一样的视觉感受，享受不一样的风情。还有搭建不一样的通往花海的道路，都是一道美丽的风景。 ', 'guilin28', ' ', '', '');
