INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2006, '漓江（阳朔-杨堤）三星游船', '5649344', '桂林市阳朔县滨江路2号水东门码头', '开船参考时间是10:30，实名制随机安排，准确开船时间以船票上的开船时间为准，不可挑选班次、船号、座位号；游船时长约5-6小时（因当天漓江水位水量等原因，会造成游览时长缩短或延长，属正常现象）', '4.5', '漓江（阳朔-杨堤）三星游船从阳朔水东门码头出发，沿江逆流而上，抵达杨堤码头。可欣赏到3大美景：黄布倒影、九马画山、杨堤风光。美丽的漓江，让我们领略两岸秀丽的风光。途经众多经典景点：九马画山，黄布倒影，20元人名币背景图，八仙过海，甲天下，鲤鱼挂壁，神笔峰，童子拜观音，雄狮爬五指山，苹果山等。这里青峰锁江，两岸翠竹茂密，形似凤尾，迎风摇曳。杨堤小镇掩映在绿色的林木之中。杨堤码头的对岸是白虎山，在山的悬崖上有一水帘洞，洞内飞泻的瀑布直流漓江，这就是“水帘洞府”。', 'guilin28', '', '', '');
