INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2018, '罗山湖露营公园', '1786986', '地址：桂林市临桂县两江镇两江大道1008号罗山湖旅游度假区露营公园', '2015年10月16日-11月30日，每周五、周六、周日对外开放，入园时间：周五为17:00，周六、周日为11:00。5-10月为水上乐园开放时间，其他时间不对外开外。', '4', ' 2015年10月中旬，罗山湖露营公园将免费开放。沙滩、草坪、湖畔，高兴睡哪儿就睡哪儿！喝啤酒，吃烧烤，你还可以是海盗船长！那些什么射箭、骑行、高尔夫，统统等你来挑战！如果大伙儿饶有兴致，再组队来场沙滩排球、沙滩足球、沙滩拔河，保证激情不输夏日玩水！ ', 'guilin28', ' ', '', '');
