INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2048, '兴坪渔村 ', '5135952', '地址：广西壮族自治区阳朔县兴坪镇 ', '', '4.7', ' 从兴坪集镇码头沿漓江相去2公里，就是兴坪渔村，传说为兴坪圩上达官显贵为择贵地迁此。兴坪渔村是一个古老的村庄，建于公元1506年，明朝万历年间，从建村到现在已有四百多年的历史。村中房屋与圩上古建筑风格相近，青砖黑瓦，坡屋面、马头墙、飞檐、画栋、雕花窗、鳞次栉比，结构独特，具有典型的明清暑期桂北民居特色，距今近500年历史，仍保存完好。整个建筑群占地1.5公顷，有传统民居48座村前漓江环绕，江对岸一带层峦叠嶂，鲤鱼山、金瓦山、元宝山、剑刀山、笔架山风采各异无不动人心弦。 ', 'guilin28', ' ', '', '');
