INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2050, '讲古堂', '5652378', '桂林市秀峰区东巷A16号', '全年 10:00-21:00。', ' ', '沧桑两千年的桂林，是一本厚厚的书，山水只是她的封面。讲古堂，东西巷仅存的四座古民居之一，是一个传播桂林本土文化，集曲艺表演、美学雅集、非遗体验、文化创意为一体的文化体验基地。每日定期有桂剧、彩调、山歌、文场、弹词、渔鼓等桂林当地特色戏曲、传统剧目演出。 历史是城市之根，文化是城市之魂。在物质生活十分丰富的今天，我们更需要从深厚的历史文化中汲取营养，充实我们的精神和灵魂！讲古堂一直以此为使命，砥砺前行，持续推进桂林历史文化与名山秀水的深度融合，让真正的桂林本土文化，走向世界！ ', 'guilin28', '', '', '');
